import { IProduct } from 'src/app/ViewModels/iproduct';
import { CompanyInfo } from './../../Shared/company-info';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
})
export class HomeComponent implements OnInit {
  companyDetails: CompanyInfo;
  isServicesDisplayed: boolean;
  mainProducts: IProduct[];
  constructor() {
    this.isServicesDisplayed = true;
    let services = ['MArketing', 'Sales', 'Managmenent'];
    this.companyDetails = new CompanyInfo(
      'IT Company',
      'https://picsum.photos/seed/picsum/200/300',
      services
    );

    this.mainProducts = [
      { ID: 1, Name: 'iphone 9', Quantity: 10, Price: 2000 },
      { ID: 2, Name: 'iphone 10', Quantity: 20, Price: 12000 },
      { ID: 3, Name: 'iphone xs', Quantity: 30, Price: 22000 },
      { ID: 36, Name: 'iphone xsf f s', Quantity:0, Price: 22000 },
      { ID: 4, Name: 'iphone s9', Quantity: 9, Price: 25000 },
      { ID: 5, Name: 'iphone x1maz0', Quantity: 2, Price: 14000 },
      { ID: 6, Name: 'iphone max xs', Quantity: 1, Price: 23000 },
    ];
  }
  toggleService() {
    this.isServicesDisplayed = !this.isServicesDisplayed;
  }
  ngOnInit(): void {}
}
<h1>Comapny Name : {{companyDetails.name}}</h1>


<div>
  <img src="{{companyDetails.logo}}" alt="Logo" />

</div>
<button class="btn btn-dark" (click)="toggleService()">
  Toggle Service
</button>
<div>

  <ul [hidden]="!isServicesDisplayed">
    <h1>Services </h1>

    <li *ngFor="let item of companyDetails.services">
      {{item}}
    </li>
  </ul>

</div>
<h1>List of our main products </h1>

<table class="table">
  <thead>
    <tr class="bg-dark text-white">
      <th>ID</th>
      <th>Name</th>
      <th>Quantity</th>
      <th>Price</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let prd of mainProducts; let i = index"
               [ngStyle]="{'background-color':'(prd.Quantity === 0)'? 'Yellow' : 'Red'}"
               [ngClass]="{'bg-danger':'(prd.Quantity === 1)'}"
               >
      <td>{{prd.ID}}</td>
      <td>{{prd.Name}}</td>

      <td>

        <!-- <div *ngIf="prd.Quantity > 0"> {{prd.Quantity}}</div>
      <div *ngIf="prd.Quantity == 0"> Out Of Stock</div> -->

        <div [ngSwitch]="prd.Quantity">
          <div *ngSwitchCase="0">Out of Stock</div>
          <div *ngSwitchCase="1">Last Item</div>
          <div *ngSwitchDefault>{{prd.Quantity}}</div>
        </div>
      </td>

      <td>{{prd.Price}}</td>

    </tr>
  </tbody>
</table>
